package ch14;import java.util.Arrays;import java.util.Collections;import java.util.List;public class LiteralPetCreator extends PetCreator{	public static final List<Class<? extends Pet>> allTypes = 			Collections.unmodifiableList(Arrays.asList(					Pet.class,Dog.class,Mutt.class			));			@Override	public List<Class<? extends Pet>> types() { 		return allTypes;	}	}