package util;import java.io.BufferedReader;import java.io.File;import java.io.FileReader;import java.io.IOException;import java.util.ArrayList;import java.util.Arrays;public class TextFile extends ArrayList<String> {	public static String read(String filename) {		StringBuilder sb = new StringBuilder();		try {			BufferedReader in = new BufferedReader(new FileReader(new File(					filename).getAbsoluteFile()));			try {				String s;				while ((s = in.readLine()) != null) {					sb.append(s);					sb.append('\n');				}			} finally {				in.close();			}		} catch (IOException e) { 			throw new RuntimeException(e);		}				return sb.toString();	}		public static void write(String fileName,String text){			}		public  TextFile(String filename,String splitter) {		super(Arrays.asList(read(filename).split(splitter)));		if (get(0).equals("")) {			remove(0);		}	}		public TextFile(String filename){		this(filename, "\n");	}		public void write(String filename){			}}